Color in leaflet

wetlands &lt;- macleish_layers |&gt;
  pluck("wetlands")

leaflet() |&gt;
  addTiles()|&gt;
  addPolygons(data = wetlands, 
              color = ~SHAPE_LEN, 
              popup = ~IT_VALDESC)




wetland_pal &lt;- colorNumeric(
  palette = "viridis", 
  domain = wetlands |&gt;
    pull(SHAPE_LEN)
)

leaflet() |&gt;
  addTiles() |&gt;
  addPolygons(data = wetlands, 
              color = ~wetland_pal(SHAPE_LEN), 
              fillOpacity = 0.8, weight = 0.1, 
              popup = ~IT_VALDESC) |&gt;
  addLegend(position = "bottomleft", 
            pal = wetland_pal, 
            values = wetlands$SHAPE_LEN,
            title = "Wetland Area")

